import React from 'react'
import PokemonList from '../components/PokemonList'
import SearchForm from '../components/SearchForm'
import { useGlobalContext } from '../context'

const Home = () => {
  const { data, page, loading } = useGlobalContext()

  return (
    <main>
      <PokemonList />
      {!loading && (
        <div className='btn-container'>
          {page > 0 && <button className='prev-btn'>prev</button>}
          {data.map((item, index) => {
            return (
              <button
                key={index}
                className={`page-btn ${index === page ? 'active-btn' : null}`}
              >
                {index + 1}
              </button>
            )
          })}
          {page + 1 < data.length && <button className='next-btn'>next</button>}
        </div>
      )}
    </main>
  )
}

export default Home
